import React, { useState, useEffect } from 'react';
import {
    Grid,
    Paper,
    Typography,
    List,
    ListItem,
    ListItemText,
    Avatar,
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    TextField,
    Drawer,
    AppBar,
    Toolbar,
    IconButton,
    Divider,
    ListItemIcon,
    Menu,
    MenuItem,
    Chip
} from '@mui/material';
import {
    People,
    Group,
    Event,
    AttachMoney,
    SportsCricket,
    ChevronLeft,
    ChevronRight,
    AccountCircle,
    Edit,
    Cancel,
    Save,
    Menu as MenuIcon
} from '@mui/icons-material';
import { styled, useTheme } from '@mui/material/styles';
import api from '../utils/api';
import AdminList from '../components/AdminList';
import { Link, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

const drawerWidth = 240;

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
    ({ theme, open }) => ({
        flexGrow: 1,
        padding: theme.spacing(3),
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        marginLeft: `-${drawerWidth}px`,
        ...(open && {
            transition: theme.transitions.create('margin', {
                easing: theme.transitions.easing.easeOut,
                duration: theme.transitions.duration.enteringScreen,
            }),
            marginLeft: 0,
        }),
    })
);

const AppBarStyled = styled(AppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
    transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: `${drawerWidth}px`,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
}));

const AdminDashboard = () => {
    const [addAdminDialogOpen, setAddAdminDialogOpen] = useState(false);
    const [newAdminData, setNewAdminData] = useState({ name: '', email: '', password: '' });
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [adminCount, setAdminCount] = useState(0);
    const [playerCount, setPlayerCount] = useState(0);
    const [open, setOpen] = useState(false);
    const navigate = useNavigate();
    const theme = useTheme();
    const [anchorEl, setAnchorEl] = React.useState(null); // For admin profile menu
    const [adminProfile, setAdminProfile] = useState(null); // Hold current admin data
    const [editingProfile, setEditingProfile] = useState(false); // Toggle edit mode
    const [editedName, setEditedName] = useState(''); // Hold edited name
    const [editedEmail, setEditedEmail] = useState(''); // Hold editedEmail
    const [auctions, setAuctions] = useState([]);// State for auctions
    const [admins, setAdmins] = useState([]);

    useEffect(() => {
        const fetchAdminCount = async () => {
            try {
                const response = await api.get('/admin/count');
                setAdminCount(response.data.count);
            } catch (error) {
                console.error('Error fetching admin count:', error);
                setError('Failed to load admin count.');
            }
        };
        const fetchPlayerCount = async () => {
            try {
                const response = await api.get('/players/count');
                setPlayerCount(response.data.count);
            } catch (error) {
                console.error('Error fetching player count:', error);
                setError('Failed to load player count.');
            }
        };
        const fetchAdminProfile = async () => {
            try {
                //  Replace with an API call to get the logged-in admin's profile
                //  Example:  /admin/profile
                //  or /admin/me
                const response = await api.get('/admin/admins');
                setAdminProfile(response.data[0]); // Assuming first admin is the logged-in one
                setEditedName(response.data[0].name);
                setEditedEmail(response.data[0].email);
            } catch (error) {
                console.error('Error fetching admin profile:', error);
                setError('Failed to load admin profile.');
            }
        };
        const fetchAuctions = async () => {
            try {
                // Replace with API call to /auctions/upcoming or similar
                const response = await api.get('/auctions');
                setAuctions(response.data); // Store the auctions
            } catch (error) {
                console.error("Error fetching auctions:", error);
                setError("Failed to load auctions.");
            }
        };

        fetchAdminCount();
        fetchPlayerCount();
        fetchAdminProfile();
        fetchAuctions(); // Fetch the auctions
    }, []);
    const fetchAdmins = async () => {
        try {
            const response = await api.get('/admin/admins');
            setAdmins(response.data);
        } catch (error) {
            console.error('Error fetching admins:', error);
            setError('Failed to load admins.');
        }
    };

    const handleAddAdminDialogOpen = () => {
        setAddAdminDialogOpen(true);
    };

    const handleAddAdminDialogClose = () => {
        setAddAdminDialogOpen(false);
        setNewAdminData({ name: '', email: '', password: '' });
        setError('');
        setSuccessMessage('');
    };

    const handleNewAdminDataChange = (e) => {
        setNewAdminData({ ...newAdminData, [e.target.name]: e.target.value });
    };

    const handleCreateNewAdmin = async () => {
        console.log("Creating new admin with data:", newAdminData);
        try {
            const response = await api.post('/admin/register', newAdminData);
            console.log("Admin creation response:", response);
            if (response.status === 201) {
                setSuccessMessage('Admin added successfully!');
                setAddAdminDialogOpen(false);
                setNewAdminData({ name: '', email: '', password: '' });
                fetchAdmins(); // Refresh the admin list
            } else {
                setError(response.data.message || 'Failed to add admin');
            }
        } catch (error) {
            console.error('Error creating admin:', error);
            setError(error.response?.data?.message || 'Failed to add admin');
        }
    };

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };
    const handleLogout = () => {
        Cookies.remove('token');
        Cookies.remove('admin');
        navigate('/');
    };
    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleProfileMenuClose = () => {
        setAnchorEl(null);
    };
    const handleEditProfile = () => {
        setEditingProfile(true);
        handleProfileMenuClose();
    };
    const handleCancelEdit = () => {
        setEditingProfile(false);
        setEditedName(adminProfile.name);
        setEditedEmail(adminProfile.email);
    };
    const handleSaveProfile = async () => {
        try {
            // Update profile data in backend
            const response = await api.put(`/admin/admins/${adminProfile._id}`, {
                name: editedName,
                email: editedEmail,
            });

            if (response.status === 200) {
                setSuccessMessage('Profile updated successfully!');
                setEditingProfile(false);

                // Update adminProfile in the state after successful update
                setAdminProfile(prevProfile => ({ ...prevProfile, name: editedName, email: editedEmail }));

            } else {
                setError(response.data.message || 'Failed to update profile');
            }
        } catch (error) {
            console.error('Error updating profile:', error);
            setError(error.response?.data?.message || 'Failed to update profile');
        }
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    const menuId = 'primary-search-account-menu';
    const renderMenu = (
        <Menu
            anchorEl={anchorEl}
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            id={menuId}
            keepMounted
            transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
        >
            <MenuItem onClick={handleEditProfile}>
                <ListItemIcon>
                    <Edit fontSize="small" />
                </ListItemIcon>
                Edit Profile
            </MenuItem>
        </Menu>
    );
    return (
        <Box sx={{ display: 'flex' }}>
            <AppBarStyled position="fixed" open={open}>
                <Toolbar>
                    <IconButton
                        size="large"
                        edge="start"
                        color="inherit"
                        aria-label="open drawer"
                        onClick={handleDrawerOpen}
                        sx={{ mr: 2, ...(open && { display: 'none' }) }}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
                        Admin Cricket Auction
                    </Typography>
                    {adminProfile && (
                        <IconButton
                            size="large"
                            edge="end"
                            aria-label="account of current user"
                            aria-controls={menuId}
                            aria-haspopup="true"
                            onClick={handleProfileMenuOpen}
                            color="inherit"
                        >
                            <AccountCircle />
                        </IconButton>
                    )}
                    <Button color="inherit" onClick={handleLogout}>
                        Logout
                    </Button>
                </Toolbar>
            </AppBarStyled>
            <Drawer
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                        width: drawerWidth,
                        boxSizing: 'border-box',
                    },
                }}
                variant="persistent"
                anchor="left"
                open={open}
            >
                <DrawerHeader>
                    <IconButton onClick={handleDrawerClose}>
                        {theme.direction === 'ltr' ? <ChevronLeft /> : <ChevronRight />}
                    </IconButton>
                </DrawerHeader>
                <Divider />
                <List>
                    <ListItem button component={Link} to="/dashboard">
                        <ListItemIcon>
                            <People />
                        </ListItemIcon>
                        <ListItemText primary="Dashboard" />
                    </ListItem>
                    <ListItem button component={Link} to="/players">
                        <ListItemIcon>
                            <SportsCricket />
                        </ListItemIcon>
                        <ListItemText primary="Players" />
                    </ListItem>
                    <ListItem button component={Link} to="/auctions">
                        <ListItemIcon>
                            <Event />
                        </ListItemIcon>
                        <ListItemText primary="Auctions" />
                    </ListItem>
                </List>
            </Drawer>
            <Main open={open}>
                <DrawerHeader />
                {error && <Typography color="error">{error}</Typography>}
                {successMessage && <Typography color="success">{successMessage}</Typography>}
                {adminProfile && editingProfile ? (
                    <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
                        <Typography variant="h6" gutterBottom>Edit Profile</Typography>
                        <TextField
                            label="Name"
                            fullWidth
                            margin="normal"
                            value={editedName}
                            onChange={(e) => setEditedName(e.target.value)}
                        />
                        <TextField
                            label="Email Address"
                            fullWidth
                            margin="normal"
                            value={editedEmail}
                            onChange={(e) => setEditedEmail(e.target.value)}
                            disabled={adminProfile.email === '21bmiit110@gmail.com'}
                        />
                        <Box sx={{ mt: 2 }}>
                            <Button variant="contained" color="primary" onClick={handleSaveProfile}>
                                <Save sx={{ mr: 1 }} />Save
                            </Button>
                            <Button onClick={handleCancelEdit} sx={{ ml: 1 }}>
                                <Cancel sx={{ mr: 1 }} />Cancel
                            </Button>
                        </Box>
                    </Paper>
                ) : (
                    adminProfile && (
                        <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
                            <Typography variant="h6" gutterBottom>Admin Profile</Typography>
                            <Typography>Name: {adminProfile.name}</Typography>
                            <Typography>Email: {adminProfile.email}</Typography>
                        </Paper>
                    )
                )}
                <Grid container spacing={3}>
                    <Grid item xs={12} sm={6} md={3}>
                        <Paper elevation={3} sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
                            <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                                <People />
                            </Avatar>
                            <div>
                                <Typography variant="h6">Total Admins</Typography>
                                <Typography variant="subtitle1">{adminCount}</Typography>
                            </div>
                        </Paper>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <Paper elevation={3} sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
                            <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                                <People />
                            </Avatar>
                            <div>
                                <Typography variant="h6">Total Players</Typography>
                                <Typography variant="subtitle1">{playerCount}</Typography>
                            </div>
                        </Paper>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <Paper elevation={3} sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
                            <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                                <Group />
                            </Avatar>
                            <div>
                                <Typography variant="h6">Total Teams</Typography>
                                <Typography variant="subtitle1">8</Typography>
                            </div>
                        </Paper>
                    </Grid>
                    {auctions.length > 0 && (
                        <Grid item xs={12}>
                            <Paper elevation={3} sx={{ p: 2 }}>
                                <Typography variant="h6" gutterBottom>
                                    Upcoming Auctions
                                </Typography>
                                <List>
                                    {auctions.map((auction) => (
                                        <ListItem divider key={auction._id}>
                                            <ListItemText
                                                primary={new Date(auction.startTime).toLocaleString()}
                                                secondary={`Player: ${auction.player?.name || 'N/A'}`}
                                            />
                                            <Chip label="Upcoming" color="success" />
                                        </ListItem>
                                    ))}
                                </List>
                            </Paper>
                        </Grid>
                    )}
                    <Grid item xs={12}>
            <AdminList admins={admins} fetchAdmins={fetchAdmins} setError={setError}/>
                    </Grid>
                    <Grid item xs={12}>
                        <Button variant="contained" color="primary" onClick={handleAddAdminDialogOpen}>
                            Add New Admin
                        </Button>
                    </Grid>
                </Grid>
            </Main>
            {renderMenu}
        </Box>
    );
};

export default AdminDashboard;
