import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton, Box, Divider } from '@mui/material';
import { Link } from 'react-router-dom';
import { People, Event, SportsCricket, ChevronLeft } from '@mui/icons-material';
import { styled } from '@mui/material/styles'; // Import styled

const ToolbarStyle = styled('div')(({ theme }) => ({ // Styled div instead of Toolbar
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
}));

const Sidebar = ({ open, onClose }) => {
    return (
        <Drawer
            anchor="left"
            open={open}
            onClose={onClose}
            sx={{
                width: 240,
                flexShrink: 0,
                '& .MuiDrawer-paper': {
                    width: 240,
                    boxSizing: 'border-box',
                },
            }}
        >
            <ToolbarStyle>
                <IconButton onClick={onClose}>
                    <ChevronLeft />
                </IconButton>
            </ToolbarStyle>
            <Divider />
            <List>
                <ListItem button component={Link} to="/dashboard">
                    <ListItemIcon>
                        <People />
                    </ListItemIcon>
                    <ListItemText primary="Dashboard" />
                </ListItem>
                <ListItem button component={Link} to="/players">
                    <ListItemIcon>
                        <SportsCricket />
                    </ListItemIcon>
                    <ListItemText primary="Players" />
                </ListItem>
                <ListItem button component={Link} to="/auctions">
                    <ListItemIcon>
                        <Event />
                    </ListItemIcon>
                    <ListItemText primary="Auctions" />
                </ListItem>
            </List>
        </Drawer>
    );
};

export default Sidebar;
