import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import AdminDashboard from './pages/AdminDashboard'; // Updated path
import PlayerManagement from './pages/PlayerManagement'; // Updated path
import AuctionManagement from './pages/AuctionManagement'; // Updated path
import AdminLogin from './pages/AdminLogin'; // Updated path
import BidderDashboard from './pages/BidderDashboard'; // Updated path
import BidderRegister from './pages/BidderRegister'; // Updated path
import BidderLogin from './pages/BidderLogin'; // Updated path
import AuctionPage from './pages/AuctionPage'; // Updated path
import Home from './pages/Home';
import Navbar from './components/Navbar';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        const token = Cookies.get('token');
        const admin = Cookies.get('admin');
        setIsLoggedIn(!!token);
        setIsAdmin(admin === 'true');
    }, []);

    return (
        <Router>
            <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} isAdmin={isAdmin} />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/admin-login" element={!isLoggedIn || !isAdmin ? <AdminLogin setIsLoggedIn={setIsLoggedIn} setIsAdmin={setIsAdmin} /> : <Navigate to="/admin-dashboard" />} />
                <Route path="/bidder-register" element={<BidderRegister />} />
                <Route path="/bidder-login" element={!isLoggedIn || isAdmin ? <BidderLogin setIsLoggedIn={setIsLoggedIn} setIsAdmin={setIsAdmin} /> : <Navigate to="/bidder-dashboard" />} />
                <Route path="/admin-dashboard" element={isLoggedIn && isAdmin ? <AdminDashboard /> : <Navigate to="/admin-login" />} />
                <Route path="/bidder-dashboard" element={isLoggedIn && !isAdmin ? <BidderDashboard /> : <Navigate to="/bidder-login" />} />
                <Route path="/players" element={isLoggedIn && isAdmin ? <PlayerManagement /> : <Navigate to="/bidder-login" />} />
                <Route path="/auctions" element={isLoggedIn && isAdmin ? <AuctionManagement /> : <Navigate to="/bidder-login" />} />
                <Route path="/auction-page" element={isLoggedIn && !isAdmin ? <AuctionPage /> : <Navigate to="/bidder-login" />} />
            </Routes>
        </Router>
    );
}

export default App;
