import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import AdminLogin from './pages/AdminLogin';
import AdminDashboard from './pages/AdminDashboard';
import PlayerManagement from './pages/PlayerManagement';
import AuctionScheduling from './pages/AuctionScheduling';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = Cookies.get('token');
        setIsLoggedIn(!!token);
    }, []);

    return (
        <Router>
            <Routes>
                <Route path="/" element={isLoggedIn ? <Navigate to="/dashboard" /> : <Navigate to="/login" />} />
                <Route path="/login" element={!isLoggedIn ? <AdminLogin setIsLoggedIn={setIsLoggedIn} /> : <Navigate to="/dashboard" />} />
                <Route path="/dashboard" element={isLoggedIn ? <AdminDashboard /> : <Navigate to="/login" />} />
                <Route path="/players" element={isLoggedIn ?  <PlayerManagement /> : <Navigate to="/login" />} />
                <Route path="/auctions" element={isLoggedIn ? <AuctionScheduling /> : <Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
